export async function fetchData(){try{const t=await fetch("https://localhost:5000/view-user-data");if(!t.ok)throw new Error("Network response was not ok");const o=await t.json();return sessionStorage.setItem("gameData",JSON.stringify(o)),o}catch(t){console.error("Failed to fetch data:",t)}}window.addEventListener("DOMContentLoaded",(()=>{const t=sessionStorage.getItem("gameData");t?(console.log("Data loaded from sessionStorage"),updateVisualization(JSON.parse(t))):(console.log("No data in sessionStorage. Fetching from API..."),fetchData())}));